    state = {
        showRow: false
    }

    toggleRow = () => {
        const currentState = this.state.showRow;
        this.setState({ showRow: !currentState });
    }

    render() {
      return (
        <section className="sectionContainer">
            <h2 className="sectionMainTitle">熱門文章</h2>
